CREATE TABLE IF NOT EXISTS fics (
    id INT AUTO_INCREMENT PRIMARY KEY,
    Name VARCHAR(255) NOT NULL,
    Fandom VARCHAR(255) NOT NULL,
    Ships VARCHAR(255) NOT NULL,
    Rating VARCHAR(255) NOT NULL,
    Link VARCHAR(255),
    Review TEXT,
    Abstract TEXT
);

