INSERT INTO fics (Name, Fandom, Ships, Rating, Link, Review, Abstract) VALUES ('Dime quien ama de verdad', 'Harry Potter', '', '', 'https://www.fanfiction.net/s/13442000/1/Dime-quien-ama-de-verdad', '', 'La muerte de Albus Dumbledore devela los secretos mejor guardados del director de Hogwarts.   Cuan importante es la sangre ante la traici on y el deseo de venganza? Hermione Granger y Draco Malfoy crearan una nueva alianza para derrotar a sus enemigos intentando mantenerse profesionales y no caer irremediablemente en la tentaci on.');
